SELECT * FROM ShippedLogs
WHERE (Ordername like '%{{archive_table.searchText || ""}}%' 
			 OR Vessel like '%{{archive_table.searchText || ""}}%'
			 OR Log like '%{{archive_table.searchText || ""}}%'
			 OR Docket like '%{{archive_table.searchText || ""}}%'
			 OR Grade like '%{{archive_table.searchText || ""}}%'
			 OR Supplier like '%{{archive_table.searchText || ""}}%'
			 OR ShipDate like '%{{archive_table.searchText || ""}}%'
			) 
ORDER BY {{archive_table.sortOrder.column || 'Log'}} {{archive_table.sortOrder.order || "ASC"}}
LIMIT {{archive_table.pageSize}}
OFFSET {{(archive_table.pageNo - 1) * archive_table.pageSize}};
